#include<bits/stdc++.h>
using namespace std;

class Solution {
public:
    //超时了tmd
    int maximumCandies(vector<int>& candies, long long k) {
        long long sum = accumulate(candies.begin(),candies.end(),0);
        int ans = sum/k;//贪心吧, 认为可以拿到均分的结果
        int n = candies.size();
        if(ans == 0)
            return 0;
        
        int could;//能分出来的堆数
        while(true){
            could = candies.size();
            for(int i=0; i<n; i++){
                if(candies[i] < ans){
                    could--;
                }
                else if(candies[i] >= 2*ans){
                    could+=candies[i]/ans -1;//也可能分出来多堆ans
                }
            }
            if(could < k){
                ans--;
                if(ans==0)
                    return 0;
            }
            else
                break;
        }
        return ans;
    }
};

